#  The dictionary below will hold the starting and
#  ending indexes in the slice. The key will be the
#  starting index and the value will be the ending
#  index.
index_dictionary = {}

def merge_the_tools(string,k):

    for index in range(len(string)):
        if index == 0:
            index_dictionary[index] = index + k
            print_non_duplicates(string[index:index+k])
        elif index in index_dictionary.values():
            index_dictionary[index] = index + k
            print_non_duplicates(string[index:index+k])
        else:
            pass
    

def print_non_duplicates(slice):
    print(''.join(list(dict.fromkeys(slice))))


sample_string = ''
string_length = 3452

merge_the_tools(sample_string,string_length)